import { useState } from 'react';
import "./App.css";

function App() {
  const [formData, setFormData] = useState({
    ID: '',
    AccountID: ''
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const onSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('/details', {
        method: "POST",
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(formData)
      });

      if (response.ok) {
        console.log("Data posted successfully");
        alert("Data submitted successfully!");
      } else {
        console.error("Something went wrong");
      }
    } catch (error) {
      console.error("Error:", error.message);
    }
  };

  const executePythonScript = async () => {
    try {
      const response = await fetch('/execute');
      if (response.ok) {
        console.log("Python script executed successfully");
        alert("Python script executed successfully");
      } else {
        console.error("Error executing Python script");
      }
    } catch (error) {
      console.error("Error:", error.message);
    }
  };

  return (
    <div className="auth-form-container">

      <form onSubmit={onSubmit} className="login-form">
      <label htmlFor="ID">ID</label>
        <input type="text" name="ID" value={formData.ID} onChange={handleInputChange} />
        <br></br>
        <label htmlFor="AccountID">AccountID</label>
        <input type="text" name="AccountID" value={formData.AccountID} onChange={handleInputChange} />

        <br></br>
        <button >Add Account</button>
      <button onClick={executePythonScript}>Update Account</button>
      </form>
    </div>
  );
}

export default App;
