import {creatref} from 'react';
import './App.css';

function App() {
  const fileInput=creatref();

  const onSubmit= async(e) => {
    e.preventDefault();
    const formData=new formData();
    formData("avatar",fileInput.current.value);

    try{
      const response= await fetch('/details',{
        method:"POST",
        body:formData
      });
      const parsedResponse=await response.json();
      if(response.ok){
        alert("File uploaded")
      }else{
        console.error("Something went wrong");
      }
    }
    catch (e){
      console.error(e.message);
    }
  }
  return (
    <div className="App">
     <form onSubmit={onSubmit}>
      <input type="text" name="avtar" ref ={fileInput}/>
      <input type="text"name="furious"/>
      <input type="submit" value="submit"/>
     </form>
    </div>
  );
}

export default App;
