import { useState } from 'react';
import { spawn } from 'child_process';

function App() {
  const [formData, setFormData] = useState({
    ID: '',
    AccountID: ''
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const onSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('/details', {
        method: "POST",
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(formData)
      });

      if (response.ok) {
        console.log("data posted")
        alert("Data submitted successfully!");

        // Call Python script after successful form submission
        const pythonScript = spawn('python', ['C:/user-datasubmission-react/frontend/src/app.py']);

        pythonScript.stdout.on('data', (data) => {
          console.log(`Python script output: ${data}`);
        });

        pythonScript.stderr.on('data', (data) => {
          console.error(`Python script error: ${data}`);
        });

        pythonScript.on('close', (code) => {
          console.log(`Python script process exited with code ${code}`);
        });
      } else {
        console.error("Something went wrong");
      }
    } catch (error) {
      console.error("Error:", error.message);
    }
  };

  return (
    <div className="App">
      <form onSubmit={onSubmit}>
        ID<input type="text" name="ID" value={formData.ID} onChange={handleInputChange} />
        <br></br>
        AccountID <input type="email" name="AccountID" value={formData.AccountID} onChange={handleInputChange} />
        <br></br>
        <input type="submit" value="ADD" />
      </form>
    </div>
  );
}

export default App;
